mapper.update:
update review
set product_id=#{productId}, user_id=#{userId}, content=#{content}, created=#{created}, 
modified=#{modified}
where id=#{id}

mapper.delete:
update review
delete from review
where id=#{id}

mapper.select:
select r.id, r.product_id as product_id, r.user_id as user_id, u.account, r.content, r.created, r.modified
from review r left join user u on r.user_id = u.id
where product_id=#{productId}
